import axios from "axios";
import React, {useState, useEffect} from "react";

const useFlip = (initialFlipState = true) => {
    const [isFlipped, setFlipped] = useState(initialFlipState);

    const flip = () => {
        setFlipped(isUp => !isUp);
    };

    return [isFlipped, flip];
}

const useAxios = (keyInLs, baseUrl) => {
    const [response, setResponse] = useLocalStorage(keyInLs);

    const addResponseData = async (formatter = data => data, restOfUrl = "") => {
        const response = await axios.get(`${baseUrl}${restOfUrl}`);
        setResponse(data => [...data, formatter(response.data)]);
    };
    const clearResponses = () => setResponse([]);

    return [response, addResponseData, clearResponses];
}

function useLocalStorage(key, initialValue = []) {
    if (localStorage.getItem(key)) {
      initialValue = JSON.parse(localStorage.getItem(key));
    }
    const [value, setValue] = useState(initialValue);

    useEffect(() => {
      localStorage.setItem(key, JSON.stringify(value));
    }, [value, key]);

    return [value, setValue];
  }

export default [useFlip, useAxios, useLocalStorage];
